package javase;
import java.util.*;

public class Conditionalstst {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
// if else, else if, switch, break
		System.out.println("Everyone's cup of tea\n");
		Scanner sc = new Scanner(System.in);
		int age = sc.nextInt();
		
		if(age>18){
			System.out.println("is an adult\n");
			
		}
		else {
			System.out.println("not an adult\n");
			
		}
	}

}
